import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../../../assets/styles/buttons/cta-button3.css';
import '../../../../assets/styles/titles/titles.css';
import '../../../../assets/styles/switch/beta-switch.css'; 
import '../../../../assets/styles/layout/beta/beta-section.css'; 

const BetaSection = () => {
  const [isLightMode, setIsLightMode] = useState(false);
  const [isTeacherMode, setIsTeacherMode] = useState(false);  
  const navigate = useNavigate();

  useEffect(() => {
    const observer = new MutationObserver(() => {
      const isLightModeActive = document.body.classList.contains('light-mode');
      setIsLightMode(isLightModeActive);
    });

    observer.observe(document.body, {
      attributes: true,
      attributeFilter: ['class'],
    });

    return () => {
      observer.disconnect();
    };
  }, []);

  const handleBetaButtonClick = () => {
    navigate('/beta'); 
  };

  const handleTeacherButtonClick = () => {
    setIsTeacherMode(!isTeacherMode);  
  };

  return (
    <>
      <div className="beta-content">
        <button className="emoji-button">ðŸŽ“</button>
        <button className="emoji-button teacher-button" onClick={handleTeacherButtonClick}>ðŸ“š</button>

        <div className="beta-text">
          <h1 className='beta-title'>
            {isTeacherMode 
              ? <span style={{ color: isLightMode ? '#000' : '#fff' }}>Organiza tu clase</span>
              : <>
                  <span style={{ color: isLightMode ? '#000' : '#FFF' }}>RÃ¡pido</span>,{' '}
                  <span style={{ color: isLightMode ? '#000' : '#FFF' }}>preciso</span>,{' '}
                  <span style={{ color: isLightMode ? '#000' : '#FFF' }}>siempre sincronizado</span>.
                </>
            }
          </h1>
          <div className="email-input-container">
            <input
              type="email"
              className="email-input"
              placeholder={isTeacherMode ? "Ingresa tu correo institucional" : "Ingresa tu correo personal"}
            />
            <button 
              className="cta-button3" 
              onClick={handleBetaButtonClick}
              style={{ backgroundColor: isTeacherMode ? '#3da8c9' : '#1BA77E' }}  // Cambiar color del botÃ³n segÃºn el modo
            >
              {isTeacherMode ? "Reservar" : "Reservar"}
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default BetaSection;
