import React, { useState } from 'react';
import '../../../../assets/styles/buttons/cta-button3.css';
import '../../../../assets/styles/titles/titles.css';
import '../../../../assets/styles/switch/beta-switch.css'; 
import '../../../../assets/styles/layout/beta/beta-section.css'; 

const BetaForm = () => {
  const [formData, setFormData] = useState({
    nombre: '',
    correo: '',
    usuario: '',
    semestre: '',
    universidad: '',
    carrera: '',
    boleta: ''
  });

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic for form submission (e.g., send data to the server)
    console.log('Form submitted', formData);
  };

  return (
    <div className="form-container">
      <h1>Acceso Beta</h1>
      <h2>Cuenta Estudiante</h2>
      <form id="betaEstudianteForm" onSubmit={handleSubmit}>
        <input 
          type="text" 
          id="nombre" 
          name="nombre" 
          placeholder="Nombre Completo" 
          value={formData.nombre} 
          onChange={handleInputChange} 
          required
        />
        <input 
          type="email" 
          id="correo" 
          name="correo" 
          placeholder="Correo ElectrÃ³nico" 
          value={formData.correo} 
          onChange={handleInputChange} 
          required
        />
        <input 
          type="text" 
          id="usuario" 
          name="usuario" 
          placeholder="@usuario-a-reservar" 
          value={formData.usuario} 
          onChange={handleInputChange} 
        />
        <input 
          type="text" 
          id="semestre" 
          name="semestre" 
          placeholder="Semestre" 
          value={formData.semestre} 
          onChange={handleInputChange} 
        />
        <input 
          type="text" 
          id="universidad" 
          name="universidad" 
          placeholder="Universidad" 
          value={formData.universidad} 
          onChange={handleInputChange} 
        />
        <input 
          type="text" 
          id="carrera" 
          name="carrera" 
          placeholder="Carrera" 
          value={formData.carrera} 
          onChange={handleInputChange} 
        />
        <input 
          type="text" 
          id="boleta" 
          name="boleta" 
          placeholder="No. Boleta (Opcional)" 
          value={formData.boleta} 
          onChange={handleInputChange} 
        />
        <button type="submit">Solicitar acceso</button>
      </form>
    </div>
  );
};

export default BetaForm;
